sources.style/03-organisms/chapter-link.scss:
.o-chapterLink {
  @include medium {
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
}

.o-chapterLink__content {
  max-width: 78ch;

  & > * + * {
    margin-top: var(--spaceM);
  }
}

.o-chapterLink__progression {
  display: flex;
  align-items: baseline;

  & > * + * {
    margin-left: var(--spaceM);
  }
}

.o-chapterLink__links {
  display: flex;

  & > * + * {
    margin-left: var(--spaceM);
  }
}

sources.style/03-organisms/chapter-part.scss:
.o-chapterPart__content {
  display: none;
  margin-top: var(--spaceM);

  .o-chapterPart--open & {
    display: block;
  }

  &,
  .o-chapterPart__intro,
  .o-chapterPart__theory,
  .o-chapterPart__aside {
    & > * + * {
      margin-top: var(--spaceM);
    }
  }

  & > * + * {
    @include large {
      margin-top: var(--spaceL);
    }
  }
}

@include large {
  .o-chapterPart__intro {
    display: flex;
    align-items: flex-end;
  }

  .o-chapterPart__theory {
    flex: 3;
    margin-right: var(--spaceL);
  }

  .o-chapterPart__aside {
    flex: 2;
  }
}

sources.style/03-organisms/course-map.scss:
