sources.style/00-protons/fonts.scss:
@mixin font($font-family, $file-path, $font-weight, $font-style) {
  @font-face {
    font-family: $font-family;
    src: url('#{$file-path}.woff2') format('woff2'),
      url('#{$file-path}.woff') format('woff');
    font-weight: $font-weight;
    font-style: $font-style;
  }
}

@include font('Oswald', '../assets/fonts/Oswald/Oswald-Regular', 400, normal);
@include font('Oswald', '../assets/fonts/Oswald/Oswald-Light', 300, normal);
@include font('Roboto', '../assets/fonts/Roboto/Roboto-Regular', 400, normal);
@include font('Roboto', '../assets/fonts/Roboto/Roboto-Bold', 900, normal);
@include font('Roboto', '../assets/fonts/Roboto/Roboto-Italic', 400, italic);

:root {
  --fontFamilyTitle: Oswald, sans-serif;
  --fontFamilyMain: Roboto, sans-serif;
  --fontFamilyCode: monospace;

  --fontSizeXXL: 80px;
  --fontSizeXL: 32px;
  --fontSizeL: 24px;
  --fontSizeM: 18px;
  --fontSizeS: 14px;

  --fontWeightBold: 900;
  --fontWeightThin: 300;

  --lineHeightMain: 1.5;
  --lineHeightLarge: 2;
}

sources.style/01-atoms/card-title.scss:
.a-cardTitle {
  font-family: var(--fontFamilyTitle);
  font-size: var(--fontSizeM);
  font-weight: var(--fontWeightThin);
  text-transform: uppercase;
}

sources.style/01-atoms/checkmark.scss:
@keyframes checked {
  50% {
    transform: scale(1.2);
  }
}

.a-checkmark {
  user-select: none;
}

.a-checkmark__input {
  display: none;

  &:checked ~ .a-checkmark__label .a-checkmark__icon {
    filter: none;
    animation: checked 0.5s;
  }
}

.a-checkmark__icon {
  cursor: pointer;
  height: 24px;
  width: 24px;
  fill: var(--colorSuccess);
  filter: grayscale(100%);
}

sources.style/01-atoms/code.scss:
.a-code {
  font-size: var(--fontSizeM);
}

.a-code__language {
  font-family: var(--fontFamilyTitle);
  color: var(--colorCode);
  margin-bottom: var(--spaceXS);
}

.a-code__content {
  font-family: var(--fontFamilyCode);
  line-height: var(--lineHeightMain);
  white-space: pre-line;
  color: var(--colorTextMain);
}

sources.style/01-atoms/icon.scss:
.a-icon {
  opacity: 0.5;
  width: 18px;
  height: 18px;
}

.a-icon--large {
  opacity: 0.3;
  width: 50px;
  height: 50px;
}

.a-icon--reversed {
  fill: var(--colorTextReversed);
  opacity: 1;
}

.a-icon--primary {
  fill: var(--colorPrimary);
  opacity: 1;
}

sources.style/01-atoms/footnote.scss:
.a-footnote {
  color: var(--colorTextReversed);
  font-size: var(--fontSizeSmall);
  font-family: var(--fontFamilyMain);
  line-height: var(--lineHeightMain);
}

sources.style/01-atoms/letters.scss:
.a-letters {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
}

.a-letters__input {
  margin: 0 1px 2px;
  height: 30px;
  width: 30px;
  line-height: 30px;
  text-transform: uppercase;
  font-size: var(--fontSizeM);
  border: 1px solid var(--colorInputBorder);
  text-align: center;
  font-family: var(--fontFamilyCode);

  .a-letters--success & {
    background-color: var(--colorSuccessLight);
  }
}

.a-letters__input--highlight {
  background-color: var(--colorInfo);
}

sources.style/01-atoms/link.scss:
.a-link {
  font-size: var(--fontSizeS);
  text-decoration: none;
  color: var(--colorSuccessDark);
  background: var(--colorSuccessLight);
  font-family: var(--fontFamilyMain);
  line-height: var(--lineHeightLarge);
  display: inline-block;
  padding: 0 var(--spaceS);
  border-radius: var(--borderRadius);

  &:hover {
    text-decoration: underline;
  }
}

sources.style/01-atoms/number.scss:
.a-number {
  font-family: var(--fontFamilyTitle);
  font-size: var(--fontSizeXL);
  color: var(--colorTextReversed);
  opacity: 0.7;
}

sources.style/01-atoms/indication.scss:
.a-indication {
  color: var(--colorTextSecondary);
  font-family: var(--fontFamilyMain);
  font-size: var(--fontSizeS);
  line-height: var(--lineHeightMain);
}

.a-indication--primary {
  color: var(--colorPrimary);
}

sources.style/01-atoms/paragraph.scss:
.a-paragraph {
  font-family: var(--fontFamilyMain);
  font-size: var(--fontSizeM);
  line-height: var(--lineHeightMain);
  color: var(--colorTextMain);

  code {
    font-family: var(--fontFamilyCode);
    font-weight: var(--fontWeightBold);
  }

  strong {
    font-weight: var(--fontWeightBold);
  }

  em {
    font-style: italic;
  }
}

sources.style/01-atoms/resource.scss:
.a-resource {
  text-decoration: none;
  display: flex;
  align-items: center;
  line-height: var(--lineHeightLarge);
}

.a-resource__icon {
  fill: var(--colorPrimary);
  height: 1em;
  width: 1em;
  margin-right: var(--spaceXS);
}

.a-resource__label {
  color: var(--colorPrimary);
  font-family: var(--fontFamilyMain);
  font-size: var(--fontSizeM);
  text-decoration: underline;
}

sources.style/01-atoms/title.scss:
.a-title {
  color: var(--colorPrimary);
  font-family: var(--fontFamilyTitle);
}

.a-title--level1 {
  font-size: var(--fontSizeXXL);
  text-transform: uppercase;
}

.a-title--level2 {
  font-size: var(--fontSizeXL);
}

sources.style/01-atoms/triangle.scss:
.a-triangle {
  width: 0;
  height: 0;
  border-style: solid;
  border-width: 0 6px 12px 6px;
  border-color: transparent transparent var(--colorPrimary) transparent;
  transition: transform var(--transitionDuration);
  will-change: transform;
}

.a-triangle--right {
  transform: rotate(90deg);
}

.a-triangle--down {
  transform: rotate(180deg);
}

.a-triangle--left {
  transform: rotate(270deg);
}

sources.style/01-atoms/video.scss:
.a-video__inner {
  position: relative;
  padding-top: 56.25%;
}

.a-video__iframe {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}

sources.style/02-molecules/codepen.scss:
.m-codepen {
  position: relative;
}

.m-codepen__openTip {
  display: none;

  @include wide {
    position: absolute;
    top: var(--spaceXS);
    right: 0;
    transform: translateX(100%);
    max-width: 20ch;
    display: flex;
    align-items: center;

    & > * {
      margin-left: var(--spaceS);
    }
  }
}

sources.style/02-molecules/codes.scss:
.m-codes {
  border-radius: var(--borderRadius);
  background-color: var(--colorCodeLight);
  padding: var(--spaceM);
  display: flex;

  .a-icon {
    display: none;

    @include medium {
      display: block;
      margin-right: var(--spaceM);
    }
  }
}

.m-codes__content {
  & > * + * {
    margin-top: var(--spaceM);
  }
}

sources.style/02-molecules/icon-link.scss:
.m-iconLink {
  opacity: 0.7;
  transition: transform var(--transitionDuration),
    opacity var(--transitionDuration);
  will-change: transform, opacity;

  &:hover {
    transform: scale(1.2);
    opacity: 1;
  }
}

sources.style/02-molecules/info.scss:
.m-info {
  background-color: var(--colorInfo);
  border-radius: var(--borderRadius);
  padding: var(--spaceM);
}

.m-info--warning {
  background-color: var(--colorWarning);
}

.m-info__title {
  display: flex;
  margin-bottom: var(--spaceS);

  & > * + * {
    margin-left: var(--spaceS);
  }
}

sources.style/02-molecules/part-title.scss:
.m-partTitle {
  display: flex;
  align-items: flex-end;
}

.m-partTitle--open {
  .a-triangle {
    @extend .a-triangle--down;
  }
}

.m-partTitle__opener {
  display: flex;
  align-items: center;
  cursor: pointer;

  & > * {
    margin-right: 10px;
  }
}

sources.style/02-molecules/resources.scss:
.m-resources {
  display: flex;
  flex-wrap: wrap;

  & > * {
    margin-right: var(--spaceM);
  }
}

sources.style/02-molecules/question.scss:
.m-question {
  border-radius: var(--borderRadius);
  background-color: var(--colorQuestion);
  padding: var(--spaceM);

  @include medium {
    display: flex;
    align-items: center;
  }

  .a-icon {
    display: none;

    @include medium {
      display: block;
      flex-shrink: 0;
      margin-right: var(--spaceM);
    }
  }
}

.m-question__text {
  flex-basis: 66%;
  display: flex;
}

.m-question__answer {
  flex-basis: 37%;
  text-align: center;
  margin-top: var(--spaceL);

  @include medium {
    margin-top: 0;
    margin-left: var(--spaceL);
  }

  & > * + * {
    margin-top: var(--spaceS);
  }
}

sources.style/02-molecules/twitter-account.scss:
.m-twitterAccount {
  text-decoration: none;
  color: var(--colorTextMain);
  display: block;
  text-align: center;

  &:hover {
    text-decoration: underline;
  }
}

sources.style/02-molecules/title-link.scss:
.m-titleLink {
  text-decoration: none;
  color: var(--colorPrimary);

  &:hover {
    text-decoration: underline;
  }
}

sources.style/02-molecules/word.scss:
.m-word {
  text-align: center;

  .a-letters {
    margin: var(--spaceL) auto var(--spaceM);
  }
}

sources.style/03-organisms/chapter-link.scss:
.o-chapterLink {
  @include medium {
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
}

.o-chapterLink__content {
  max-width: 78ch;

  & > * + * {
    margin-top: var(--spaceM);
  }
}

.o-chapterLink__progression {
  display: flex;
  align-items: baseline;

  & > * + * {
    margin-left: var(--spaceM);
  }
}

.o-chapterLink__links {
  display: flex;

  & > * + * {
    margin-left: var(--spaceM);
  }
}

sources.style/03-organisms/chapter-part.scss:
.o-chapterPart__content {
  display: none;
  margin-top: var(--spaceM);

  .o-chapterPart--open & {
    display: block;
  }

  &,
  .o-chapterPart__intro,
  .o-chapterPart__theory,
  .o-chapterPart__aside {
    & > * + * {
      margin-top: var(--spaceM);
    }
  }

  & > * + * {
    @include large {
      margin-top: var(--spaceL);
    }
  }
}

@include large {
  .o-chapterPart__intro {
    display: flex;
    align-items: flex-end;
  }

  .o-chapterPart__theory {
    flex: 3;
    margin-right: var(--spaceL);
  }

  .o-chapterPart__aside {
    flex: 2;
  }
}

sources.style/03-organisms/course-map.scss:
.o-courseMap__header {
  display: flex;
  align-items: baseline;
  margin-bottom: var(--spaceM);

  & > * + * {
    margin-left: var(--spaceS);
  }
}

sources.style/03-organisms/header.scss:
.o-header {
  background-color: var(--colorPrimary);
  padding: var(--spaceM) 0;
}

.o-header--chapter {
  display: flex;
  flex-direction: column;

  & > * + * {
    margin-top: var(--spaceM);
  }

  @include medium {
    flex-direction: row;
    align-items: flex-end;

    & > * + * {
      margin-left: var(--spaceM);
    }

    & > :last-child {
      margin-left: auto;
    }
  }
}

sources.style/03-organisms/footer.scss:
.o-footer {
  display: flex;
  align-items: center;
  background-color: var(--colorPrimary);
  padding: var(--spaceM);

  & > * + * {
    margin-left: var(--spaceS);
  }

  & > :last-child {
    margin-left: auto;
  }
}

.o-recommandedAccounts__list {
  margin-top: var(--spaceM);
  display: flex;
  flex-wrap: wrap;
  justify-content: center;

  & > * {
    margin: var(--spaceM) var(--spaceS);
  }
}

sources.style/03-organisms/video-list.scss:
.o-videoList__items {
  @include medium {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
  }

  & > * {
    margin-top: var(--spaceL);

    @include medium {
      width: calc(50% - var(--spaceM));
    }
  }
}

sources.style/04-layouts/main.scss:
.l-main {
  display: grid;
  grid-template-columns: 1fr minmax(0, 1000px) 1fr;
  grid-template-rows: auto 1fr auto;
  grid-gap: var(--spaceL);
  height: 100%;

  &::before,
  &::after {
    content: '';
    background-color: var(--colorPrimary);
    grid-row: 1;
    width: calc(100% + var(--spaceL));
    z-index: -1;
  }

  &::before {
    grid-column: 1;
  }

  &::after {
    grid-column: 3;
    position: relative;
    left: calc(var(--spaceL) * -1);
  }

  & > header {
    grid-column: 2;
  }

  & > main {
    padding: var(--spaceL) 0;
    grid-column: 2;
    grid-row: 2;

    & > * + * {
      margin-top: var(--spaceXL);
    }
  }

  & > footer {
    grid-column: 1 / span 3;
    grid-row: 3;
  }
}
