:root {
  --fontFamilyTitle: Oswald, sans-serif;
  --fontFamilyMain: Roboto, sans-serif;
  --fontFamilyCode: monospace;
  --fontSizeXXL: 40px;
  --fontSizeXL: 21px;
  --fontSizeL: 18px;
  --fontSizeM: 16px;
  --fontSizeS: 14px;
  --fontWeightBold: 900;
  --fontWeightThin: 300;
  --lineHeightMain: 1.5;
  --lineHeightLarge: 2;
}

:root {
  --borderRadius: 10px;
  --transitionDuration: 0.2s;
}

:root {
  --spaceXS: 4px;
  --spaceS: 8px;
  --spaceM: 16px;
  --spaceL: 32px;
  --spaceXL: 64px;
}

.a-icon {
  opacity: 0.5;
  width: 18px;
  height: 18px;
}

.a-icon--large {
  opacity: 0.3;
  width: 50px;
  height: 50px;
}

.a-icon--reversed {
  fill: var(--colorTextReversed);
  opacity: 1;
}

.a-icon--primary {
  fill: var(--colorPrimary);
  opacity: 1;
}

.a-icon--profile {
  opacity: 0.3;
  width: 25px;
  height: 25px;
  border-radius: 50%;
}

.a-icon--menu {
  width: 25px;
  height: 25px;
}

.a-separator {
  width: 247px;
}

.a-image {
  max-width: 100%;
}

.a-image--avatar {
  border-radius: 50%;
}

.a-paragraph {
  font-family: var(--fontFamilyMain);
  line-height: var(--lineHeightMain);
  color: var(--colorTextMain);
  text-align: center;
}
.a-paragraph code {
  font-family: var(--fontFamilyCode);
  font-weight: var(--fontWeightBold);
}
.a-paragraph strong {
  font-weight: var(--fontWeightBold);
}
.a-paragraph em {
  font-style: italic;
}

.a-paragraph--level1 {
  font-size: var(--fontSizeXL);
}

.a-paragraph--level2 {
  font-size: var(--fontSizeL);
}

.a-paragraph--level3 {
  font-size: var(--fontSizeM);
}

.a-title {
  color: var(--colorPrimary);
  font-family: var(--fontFamilyTitle);
}

.a-title--level1 {
  font-size: var(--fontSizeXXL);
  text-transform: uppercase;
}

.a-title--level2 {
  font-size: var(--fontSizeXL);
}

.a-button {
  background: var(--colorButton);
  width: 327px;
  height: 40px;
  display: flex;
  justify-content: center;
  align-items: center;
}

.a-button--type1 {
  border-radius: 0 0 18px 18px;
}

.a-button--type2 {
  border-radius: 18px;
}

.a-button--text {
  color: var(--colorTextReversed);
  font-family: var(--fontFamilyTitle);
}

.m-chapterTitle {
  display: flex;
}
.m-chapterTitle > * + * {
  margin-left: var(--spaceXS);
}

.m-action-bloc {
  width: 327px;
  height: 171px;
  border-radius: 18px;
  padding-top: 16px;
  display: flex;
  flex-direction: column;
}

.m-action-bloc--type1 {
  justify-content: space-between;
  align-items: center;
  background: var(--colorAction);
  -webkit-box-shadow: 3px 10px 31px -12px #737373;
  -moz-box-shadow: 3px 10px 31px -12px #737373;
  box-shadow: 3px 10px 31px -12px #737373;
}

.m-action-bloc--type2 {
  justify-content: space-between;
}

.m-correction {
  text-align: center;
}
.m-correction > * + * {
  margin-top: var(--spaceL);
}

.m-iconLink {
  opacity: 0.7;
  transition: transform var(--transitionDuration), opacity var(--transitionDuration);
  will-change: transform, opacity;
}
.m-iconLink:hover {
  transform: scale(1.2);
  opacity: 1;
}

.m-partTitle {
  display: flex;
  align-items: flex-end;
}

.m-partTitle__opener {
  display: flex;
  align-items: center;
  cursor: pointer;
}
.m-partTitle__opener > * {
  margin-right: 10px;
}

.m-resources {
  display: flex;
  flex-wrap: wrap;
}
.m-resources > * {
  margin-right: var(--spaceM);
}

.m-twitterAccount {
  text-decoration: none;
  color: var(--colorTextMain);
  display: block;
  text-align: center;
}
.m-twitterAccount:hover {
  text-decoration: underline;
}

.o-chapter > * + *,
.o-chapter__parts > * + * {
  margin-top: var(--spaceXL);
}

@media (min-width: 768px) {
  .o-chapterLink {
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
}

.o-chapterLink__content {
  max-width: 78ch;
}
.o-chapterLink__content > * + * {
  margin-top: var(--spaceM);
}

.o-chapterLink__progression {
  display: flex;
  align-items: baseline;
}
.o-chapterLink__progression > * + * {
  margin-left: var(--spaceM);
}

.o-chapterLink__links {
  display: flex;
}
.o-chapterLink__links > * + * {
  margin-left: var(--spaceM);
}

.o-chapterPart__content {
  display: none;
  margin-top: var(--spaceM);
}
.o-chapterPart--open .o-chapterPart__content {
  display: block;
}
.o-chapterPart__content > * + *,
.o-chapterPart__content .o-chapterPart__intro > * + *,
.o-chapterPart__content .o-chapterPart__theory > * + *,
.o-chapterPart__content .o-chapterPart__aside > * + * {
  margin-top: var(--spaceM);
}
@media (min-width: 980px) {
  .o-chapterPart__content > * + * {
    margin-top: var(--spaceL);
  }
}

@media (min-width: 980px) {
  .o-chapterPart__intro {
    display: flex;
    align-items: flex-end;
  }

  .o-chapterPart__theory {
    flex: 3;
    margin-right: var(--spaceL);
  }

  .o-chapterPart__aside {
    flex: 2;
  }
}
.o-courseMap__header {
  display: flex;
  align-items: baseline;
  margin-bottom: var(--spaceM);
}
.o-courseMap__header > * + * {
  margin-left: var(--spaceS);
}

.o-header {
  background-color: var(--colorPrimary);
  padding: var(--spaceM) 0;
}

.o-header--chapter {
  display: flex;
  flex-direction: column;
}
.o-header--chapter > * + * {
  margin-top: var(--spaceM);
}
@media (min-width: 768px) {
  .o-header--chapter {
    flex-direction: row;
    align-items: flex-end;
  }
  .o-header--chapter > * + * {
    margin-left: var(--spaceM);
  }
  .o-header--chapter > :last-child {
    margin-left: auto;
  }
}

.o-footer {
  display: flex;
  align-items: center;
  background-color: var(--colorPrimary);
  padding: var(--spaceM);
}
.o-footer > * + * {
  margin-left: var(--spaceS);
}
.o-footer > :last-child {
  margin-left: auto;
}

.o-recommandedAccounts__list {
  margin-top: var(--spaceM);
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
}
.o-recommandedAccounts__list > * {
  margin: var(--spaceM) var(--spaceS);
}

@media (min-width: 768px) {
  .o-videoList__items {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
  }
}
.o-videoList__items > * {
  margin-top: var(--spaceL);
}
@media (min-width: 768px) {
  .o-videoList__items > * {
    width: calc(50% - var(--spaceM));
  }
}

.l-main {
  display: grid;
  grid-template-columns: 1fr minmax(0, 1000px) 1fr;
  grid-template-rows: auto 1fr auto;
  grid-gap: var(--spaceL);
  height: 100%;
}
.l-main::before, .l-main::after {
  content: "";
  background-color: var(--colorPrimary);
  grid-row: 1;
  width: calc(100% + var(--spaceL));
  z-index: -1;
}
.l-main::before {
  grid-column: 1;
}
.l-main::after {
  grid-column: 3;
  position: relative;
  left: calc(var(--spaceL) * -1);
}
.l-main > header {
  grid-column: 2;
}
.l-main > main {
  padding: var(--spaceL) 0;
  grid-column: 2;
  grid-row: 2;
}
.l-main > main > * + * {
  margin-top: var(--spaceXL);
}
.l-main > footer {
  grid-column: 1/span 3;
  grid-row: 3;
}

